import React, { useState } from 'react';
import {
  Routes,
  Route,
  Navigate,
  Link,
  useLocation
} from 'react-router-dom';
import MainPage from './components/MainPage';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import GoodsManagement from './components/GoodsManagement';
import AddProductPage from './components/AddProductPage';
import EditProductPage from './components/EditProductPage';
import ShoppingCartDrawer from './components/ShoppingCartDrawer';
import { ShoppingCartIcon } from '@heroicons/react/24/outline';

function App() {
  const [user, setUser] = useState(null);
  const [cartOpen, setCartOpen] = useState(false);
  const [searchKeyword, setSearchKeyword] = useState('');

  // Âõ†‰∏∫ index.js Â∑≤ÁªèÁî® BrowserRouter ÂåÖË£π‰∫Ü AppÔºåÊâÄ‰ª•Ê≠§Â§ÑÂèØ‰ª•Áõ¥Êé•‰ΩøÁî® useLocation
  const location = useLocation();
  // Â¶ÇÊûúÂΩìÂâçË∑ØÂæÑÊòØ "/login" Êàñ "/register"ÔºåÂàô‰∏çÊòæÁ§∫ÊêúÁ¥¢Ê†è
  const showSearchBar = location.pathname !== '/login' && location.pathname !== '/register';

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
  };

  return (
    <>
      {/* È°∂ÈÉ®ÂØºËà™ */}
      <nav className="bg-black text-white sticky top-0 z-50 px-4 sm:px-6 lg:px-8 py-3 shadow">
        <div className="max-w-7xl mx-auto flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
          {/* Â∑¶‰æßÔºöLogo + ÂïÜÂÆ∂ÂÖ•Âè£ */}
          <div className="flex items-center space-x-6">
            <Link to="/">
              <img
                src="/logo.jpg"
                alt="ECSite"
                className="h-16 w-auto"
              />
            </Link>
            {user?.role === 'merchant' && (
              <Link
                to="/goods"
                className="text-sm border border-white px-3 py-1.5 rounded hover:bg-white hover:text-black transition"
              >
                Manage Products
              </Link>
            )}
          </div>

          {/* ‰∏≠Èó¥ÔºöÊêúÁ¥¢Ê†èÔºàÈöêËóèÂú® /login Âíå /register È°µÈù¢Ôºâ */}
          {showSearchBar && (
            <div className="w-full sm:w-1/2">
              <input
                type="text"
                placeholder="üîç Search products by name"
                value={searchKeyword}
                onChange={e => setSearchKeyword(e.target.value)}
                className="w-full px-4 py-2 text-black border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
              />
            </div>
          )}

          {/* Âè≥‰æßÔºöËÆ§ËØÅÁä∂ÊÄÅ / Ë¥≠Áâ©ËΩ¶ */}
          <div className="flex items-center space-x-4">
            {user ? (
              <>
                <span className="text-sm">Hi, {user.username}</span>
                {user.role === 'customer' && (
                  <button
                    onClick={() => setCartOpen(true)}
                    className="relative p-1 hover:text-indigo-400"
                    title="Shopping Cart"
                  >
                    <ShoppingCartIcon className="h-6 w-6" />
                  </button>
                )}
                <button
                  onClick={handleLogout}
                  className="text-sm border border-white hover:bg-white hover:text-black px-3 py-1 rounded transition"
                >
                  Logout
                </button>
              </>
            ) : (
              <Link to="/login">
                <button className="text-sm border border-white text-white px-4 py-1.5 rounded hover:bg-white hover:text-black transition">
                  Sign In
                </button>
              </Link>
            )}
          </div>
        </div>
      </nav>

      {/* È°µÈù¢Ë∑ØÁî± */}
      <Routes>
        <Route
          path="/"
          element={
            <MainPage
              user={user}
              searchKeyword={searchKeyword}
              setSearchKeyword={setSearchKeyword}
            />
          }
        />
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/register" element={<RegisterPage onRegister={handleLogin} />} />
        <Route
          path="/goods"
          element={
            user?.role === 'merchant' ? (
              <GoodsManagement user={user} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/add-product"
          element={
            user?.role === 'merchant' ? (
              <AddProductPage user={user} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route path="/edit-product/:id" element={<EditProductPage user={user} />} />
      </Routes>

      {/* Ë¥≠Áâ©ËΩ¶ÊäΩÂ±âÔºö‰ªÖÂΩìÁî®Êà∑ËßíËâ≤‰∏∫ customer Êó∂ÊòæÁ§∫ */}
      {user?.role === 'customer' && (
        <ShoppingCartDrawer
          username={user.username}
          open={cartOpen}
          setOpen={setCartOpen}
        />
      )}
    </>
  );
}

export default App;
